import { Button, CircularProgress, Container } from '@mui/material';
import React, { useEffect, useState } from 'react';

import Channels from '../electron/constants';
import { FcGoogle } from 'react-icons/fc';
import NirvanaLogo from '../subcomponents/NirvanaLogo';
import { blueGrey } from '@mui/material/colors';
// import Logo from '../../components/Logo';
import { login } from '../api/NirvanaApi';
import { useAsyncFn } from 'react-use';
import useAuth from '../providers/AuthProvider';

export default function Login() {
  const { setJwtToken } = useAuth();

  const [loginState, loginApiHandler] = useAsyncFn(login);

  const [isLoading, setIsLoading] = useState<boolean>(false);

  useEffect(() => {
    window.electronAPI.once(
      Channels.GOOGLE_AUTH_TOKENS,
      async (tokens: { access_token: string; id_token: string; refresh_token: string }) => {
        console.log('got tokens', tokens);

        setIsLoading(true);
        const loginResponse = await loginApiHandler({
          accessToken: tokens.access_token,
          idToken: tokens.id_token,
        });

        const { jwtToken, userDetails } = loginResponse;

        setJwtToken(jwtToken);

        setIsLoading(false);
      },
    );

    // todo: figure out how to clean up with the preload api
    // return () => {
    //   window.electronAPI.removeAllListeners(Channels.GOOGLE_AUTH_TOKENS);
    // };
  }, [loginApiHandler, setJwtToken]);

  // take user to browser to complete authentication
  const continueAuth = () => {
    setIsLoading(true);

    // send to main process
    window.electronAPI.auth.initiateLogin();
  };

  return (
    <Container
      maxWidth={false}
      sx={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        gap: 5,
        background: blueGrey[50],
      }}
    >
      <NirvanaLogo />

      {isLoading ? (
        <CircularProgress />
      ) : (
        <Button onClick={continueAuth} variant="outlined" color="primary" startIcon={<FcGoogle />}>
          Continue with Google
        </Button>
      )}
    </Container>
  );

  return (
    <div
      className="flex flex-col space-y-5 justify-center items-center 
    h-screen w-screen
     bg-zinc-700"
    >
      {/* <Logo className="scale-50" /> */}

      {/* ! TESTING PURPOSES */}
      <div className={'text-white flex flex-col gap-5'}>
        <button
          onClick={() =>
            setJwtToken(
              'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..bGK4DaUyuHCIRFhX5g3xQxI5SVMKR7hmte1UpmTZaVc',
            )
          }
        >
          Personal Account
        </button>

        <button
          onClick={() =>
            setJwtToken(
              'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..53TbuXaHDTivEpqVr-TP5A1vVIFznT3q_HoVxOReZKc',
            )
          }
        >
          Berkeley Email
        </button>

        <button
          onClick={() =>
            setJwtToken(
              'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..9AgmMW2LYv4QNqibvopiKAaV0GWNbChujWiY6t0OZeQ',
            )
          }
        >
          Nirvana Support
        </button>
      </div>

      {isLoading ? (
        <>
          <span className="text-white">Attempting to log you in</span>
        </>
      ) : (
        <button
          onClick={continueAuth}
          className=" text-md text-zinc-200 py-2 px-5 border border-gray-200 transition-all hover:bg-gray-200 hover:text-teal-500 rounded flex flex-row items-center space-x-2"
        >
          <FcGoogle className="text-lg" />
          <span>Continue with Google</span>
        </button>
      )}
    </div>
  );
}
